CREATE TABLE `tmodulo` (`id_modulo` int(11) NOT NULL AUTO_INCREMENT,`nombre` varchar(60) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`estatus` int(1) NOT NULL,`posicion` int NULL,`icono` varchar(20) NULL,`url_modulo` varchar(100) NULL,PRIMARY KEY (`id_modulo`) )ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ciAUTO_INCREMENT=6;CREATE TABLE `trol` (`codigo` int(1) NOT NULL AUTO_INCREMENT,`nombre` varchar(25) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`estatus` char(1) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,PRIMARY KEY (`codigo`) )ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ciAUTO_INCREMENT=3;CREATE TABLE `trol_servicio` (`id_rol_servicio` int(11) NOT NULL AUTO_INCREMENT,`id_rol` int(11) NOT NULL,`id_servicio` int(11) NOT NULL,`estatus` int(1) NOT NULL,PRIMARY KEY (`id_rol_servicio`) ,INDEX `id_rol` (`id_rol`),INDEX `id_servicio` (`id_servicio`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ciAUTO_INCREMENT=277;CREATE TABLE `tservicio` (`id_servicio` int(11) NOT NULL AUTO_INCREMENT,`id_modulo` int(11) NOT NULL,`nombre` varchar(30) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`url` varchar(255) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`estatus` int(1) NOT NULL,`posicion` int NULL,`icono` varchar(20) NULL,PRIMARY KEY (`id_servicio`) ,INDEX `id_modulo` (`id_modulo`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ciAUTO_INCREMENT=19;CREATE TABLE `tusuario` (`nombre_usu` char(25) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`clave` char(41) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`tipo` int(1) NOT NULL,`pregunta` text CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`respuesta` text CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,`intentos` int(11) NOT NULL DEFAULT 0,`estatus` char(1) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL DEFAULT '1',`nombre_completo` varchar(60) NULL,`correo` varchar(255) NULL,`id_usuario` int NOT NULL AUTO_INCREMENT,PRIMARY KEY (`id_usuario`) ,INDEX `tipo` (`tipo`))ENGINE=InnoDBDEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ci;ALTER TABLE `trol_servicio` ADD CONSTRAINT `trol_servicio_ibfk_1` FOREIGN KEY (`id_rol`) REFERENCES `trol` (`codigo`);ALTER TABLE `trol_servicio` ADD CONSTRAINT `trol_servicio_ibfk_2` FOREIGN KEY (`id_servicio`) REFERENCES `tservicio` (`id_servicio`);ALTER TABLE `tservicio` ADD CONSTRAINT `tservicio_ibfk_1` FOREIGN KEY (`id_modulo`) REFERENCES `tmodulo` (`id_modulo`);ALTER TABLE `tusuario` ADD CONSTRAINT `tusuario_ibfk_1` FOREIGN KEY (`tipo`) REFERENCES `trol` (`codigo`);